    for (my $tables_counter=0;$tables_counter<scalar(@annovar_generic_filters);$tables_counter++) {

	annovar_table_parameters({annovar_table_href => \%annovar_table,
				  table_name_ref => \$annovar_generic_filters[$tables_counter],
