     &CheckMandatoryArguments(\%mandatoryArgument, $programName);

    my $reduceIORef = \${$scriptParameterHashRef}{reduceIO};
    my $XARGSFILEHANDLE = IO::Handle->new();  #Create anonymous filehandle
    my $time = 10;
