					  sampleInfoHashRef => \%{$sampleInfoHashRef},
					  infilesLaneNoEndingHashRef => \%{$infilesLaneNoEndingHashRef},
					  jobIDHashRef => \%{$jobIDHashRef},
					  supportedCosmidReferenceHashRef => \%{$supportedCosmidReferenceHashRef},
