    for (my $sampleIDCounter=0;$sampleIDCounter<scalar(@{ ${$scriptParameterHashRef}{sampleIDs} });$sampleIDCounter++) {  #Create a sbatch for each sample

	my $sampleIDRef = \${$scriptParameterHashRef}{sampleIDs}[$sampleIDCounter];
