    close($FILEHANDLE);

    if ( (${$scriptParameterHashRef}{"p".$programName} == 1) && (${$scriptParameterHashRef}{dryRunAll} == 0) ) {
	
	&FIDSubmitJob({scriptParameterHashRef => $scriptParameterHashRef,
