    my $alignerOutDirRef = ${$argHashRef}{alignerOutDirRef} //= \${$argHashRef}{scriptParameterHashRef}{alignerOutDir};
    my $xargsFileCounter = ${$argHashRef}{xargsFileCounter} //= 0;

