	say $FILEHANDLE "## Copy file(s) to temporary directory";
	($xargsFileCounter, $xargsFileName) = &XargsMigrateContigFiles({FILEHANDLE => $FILEHANDLE,
									XARGSFILEHANDLE => $XARGSFILEHANDLE,
									arrayRef => \@{ ${$fileInfoHashRef}{contigsSizeOrdered} },
